CREATE TABLE "public"."game_settings" ("patient" uuid NOT NULL, "game_name" text NOT NULL, "configuration" jsonb NOT NULL DEFAULT jsonb_build_object(), PRIMARY KEY ("patient","game_name") , FOREIGN KEY ("patient") REFERENCES "public"."patient"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("game_name") REFERENCES "public"."game_name"("name") ON UPDATE restrict ON DELETE restrict, UNIQUE ("patient"), UNIQUE ("game_name"));COMMENT ON TABLE "public"."game_settings" IS E'Stores patients\' game settings';
